import re

dna = ""

cuts = [0]
for match in re.finditer(r"A[ACTG]TAAT", dna):
    cuts.append(match.start() +3)
cuts.append(len(dna))
print(cuts)

for i in range(1, len(cuts)):
    current_cut = cuts[i]
    previous_cut = cuts[i-1]
    frag_size = current_cut - previous_cut
    print("One Fragment size is " + str(frag_size))


    
